// make sure you install react-bootstrap! use npm install react-bootstrap bootstrap
import React from 'react';
import Head from 'next/head';

import Button from 'react-bootstrap/Button';
import Dropdown from 'react-bootstrap/Dropdown';
import Nav from 'react-bootstrap/Nav';
import Accordion from 'react-bootstrap/Accordion';
import Card from 'react-bootstrap/Card';
import { Tab, Tabs, TabContent } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

class Index extends React.Component {
  constructor(props) {
    super();
    this.state = {
      activeTab: props.activeTab || '1' }; // default - first tab

    this.handleSelect = this.handleSelect.bind(this); // bind action handler
    }

handleSelect(selectedTab) {
  // The active tab must be set into the state so that
  // the tabs component knows about the change and re-renders.
  this.setState({
    activeTab: selectedTab });
}

 render(){
     return(
         <Tabs defaultActiveKey={this.state.activeTab} transition={true} id="tabs" onSelect={this.handleSelect} style={{'margine-top':'20px'}}>
             <Tab eventKey='1' title="Catelog">Content 1</Tab>
             <Tab eventKey='2' title="Saved Classes">Content 2</Tab>
         </Tabs>
     );
 }
};

export default Index;

